INSERT INTO roadmap_days (edition_id, day_number, title, description, is_active, theme_name, teaser_text) VALUES
('da818745-0904-41b6-8182-3a338265dd68', 0, 'Pre-Forge Preparation', 'Get ready for your creator journey. Complete your KY form, pack essentials, and prepare your mindset.', true, 'Preparation', 'Your journey begins before you arrive'),
('da818745-0904-41b6-8182-3a338265dd68', 1, 'Orientation & Mindset Reset', 'Welcome session, meet your cohort, and reset your creator mindset for the week ahead.', true, 'Foundation', 'Welcome to the creator life'),
('da818745-0904-41b6-8182-3a338265dd68', 2, 'Learning Day', 'Master the art of hooks, scriptwriting, and camera techniques with hands-on sessions.', true, 'Skills', 'Level up your creator toolkit'),
('da818745-0904-41b6-8182-3a338265dd68', 3, 'Shoot Day 1', 'Put your skills into action. Create your first batch of content with mentor guidance.', true, 'Production', 'Lights, camera, create!'),
('da818745-0904-41b6-8182-3a338265dd68', 4, 'Shoot Day 2 & Edit', 'Complete your shooting schedule and dive into the editing process.', true, 'Production', 'From footage to story'),
('da818745-0904-41b6-8182-3a338265dd68', 5, 'Brand & Monetization', 'Learn to build your personal brand and discover monetization strategies.', true, 'Business', 'Turn passion into profession'),
('da818745-0904-41b6-8182-3a338265dd68', 6, 'Premiere & Farewell', 'Present your work, celebrate your growth, and graduate as a Forge Creator.', true, 'Celebration', 'Your story begins now');
